import React, { useRef } from 'react';
import Logo from './components/logo';
import Navigation from './components/navigation';
import { Heading, SubHeading } from './components/heading';
import SocialButtons from './components/socialbuttons';

function App() {
  return (
    <>
      <section className="flex flex-col h-screen container pt-20px mx-auto">
        <header className="flex justify-end">
          <Navigation
            link1='download my resume'
            link2='portfolio'
            link3='contact' 
            link4='blog'
          />
        </header>
        <Heading
          heading={`HI, I'M GLENN PHILLIPS! \n`}
        />
        <div className="flex flex-row flex-wrap h-full w-full container">
          <div className="flex flex-col justify-start grow min-w-fit">
            <SubHeading 
              subheading={`I'm a front-end developer \n from the US.`}
            />
            <SocialButtons />
          </div>
        <Logo />
        </div>
      </section>
      <main>

      <Text text='Hello world!' i={9} />
        <h1 className="text-3x1">Hello!</h1>
        <p className="text-3x1 font-bold underline">Hello!</p>

      </main>
      <footer>

      </footer>
    </>
  );
}

interface Props {
  text: string;
  i: number;
  obj?: {

  }
  handleChange?: React.ChangeEventHandler<HTMLInputElement>;
}

const Text: React.FC<Props> = ({ handleChange }) => {
  const inputRef = useRef<HTMLInputElement>(null);

  return (
    <form>
    <input ref={inputRef} onChange={handleChange}/>
    <input type="reset"/>
    <input type="submit"/>
    </form>
  );
};

export default App;
